mapper.select:
select info.id albumId, info.album_title albumTitle, info.cover_url coverUrl, info.include_track_count includeTrackCount, info.create_time, max(if(info.stat_type='0401',info.stat_num,0)) playStatNum, max(if(info.stat_type='0402',info.stat_num,0)) subscribeStatNum, max(if(info.stat_type='0403',info.stat_num,0)) buyStatNum, max(if(info.stat_type='0404',info.stat_num,0)) albumCommentStatNum from (select ai.id, ai.album_title, ai.cover_url, ai.create_time, ai.status, ai.include_track_count, stat.stat_type, stat.stat_num from album_info ai left join album_stat stat on stat.album_id = ai.id
and ai.user_id=#{vo.userId}
and ai.status = #{vo.status}
and ai.album_title like concat('%', #{vo.albumTitle}, '%')
and ai.is_deleted=0
) info group by info.id order by info.create_time desc
